import React, { useState } from 'react';

const FigmaClipboardTester: React.FC = () => {
  const [copied, setCopied] = useState(false);
  const [testType, setTestType] = useState('simpleBox');

  const generateAndCopyClipboardData = async () => {
    try {
      // Generate test clipboard data based on the selected type
      let clipboardData = createSimpleBoxData();

      // Create a Blob containing the HTML
      const blob = new Blob([clipboardData], { type: 'text/html' });
      
      // Create a ClipboardItem with the HTML format
      const item = new ClipboardItem({
        'text/html': blob
      });
      
      // Write to clipboard using the clipboard API
      await navigator.clipboard.write([item]);
      
      setCopied(true);
      setTimeout(() => setCopied(false), 2000);
    } catch (err) {
      console.error('Failed to copy to clipboard:', err);
      
      // Fallback method using a temporary element
      try {
        const tempElement = document.createElement('div');
        tempElement.innerHTML = createSimpleBoxData();
        tempElement.style.position = 'fixed';
        tempElement.style.left = '-9999px';
        document.body.appendChild(tempElement);
        
        const selection = window.getSelection();
        const range = document.createRange();
        range.selectNodeContents(tempElement);
        
        if (selection) {
          selection.removeAllRanges();
          selection.addRange(range);
          document.execCommand('copy');
          selection.removeAllRanges();
        }
        
        document.body.removeChild(tempElement);
        
        setCopied(true);
        setTimeout(() => setCopied(false), 2000);
      } catch (fallbackErr) {
        console.error('Fallback clipboard copy failed:', fallbackErr);
        alert('Copying to clipboard failed. Your browser may not support this feature.');
      }
    }
  };

  function createSimpleBoxData() {
    // Generate a random ID to ensure uniqueness
    const randomId = Math.floor(Math.random() * 100000);
    
    // Create metadata for the Figma clipboard - exactly match Figma's format
    const metadata = {
      fileKey: `NJT94DfXs1bTd0tWG89G75`,
      pasteID: randomId,
      dataType: "scene"
    };
    
    // Add a newline at the end like Figma does
    const metadataString = JSON.stringify(metadata) + "\n";
    
    // Create a simple rectangle node exactly like Figma's format
    const rectangleNode = {
      id: "0:1",
      type: "RECTANGLE",
      name: "Simple Box",
      blendMode: "NORMAL",
      absoluteBoundingBox: {
        x: 0,
        y: 0,
        width: 100,
        height: 100
      },
      constraints: {
        vertical: "TOP",
        horizontal: "LEFT"
      },
      fills: [
        {
          type: "SOLID",
          color: {
            r: 0,
            g: 0.4392156862745098,
            b: 0.9529411764705882,
            a: 1
          }
        }
      ],
      strokes: [],
      strokeWeight: 0,
      strokeAlign: "CENTER",
      effects: [],
      cornerRadius: 8
    };
    
    // Base64 encode the metadata and node data
    const metadataBase64 = btoa(metadataString);
    const nodeDataBase64 = btoa(JSON.stringify(rectangleNode));
    console.log(nodeDataBase64);
    
    // Generate HTML structure exactly matching Figma's format
    return `<meta charset='utf-8'><meta charset="utf-8"><span data-metadata="<!--(figmeta)eyJmaWxlS2V5IjoiTkpUOTREZlhzMWJUZDB0V0c4OUc3NSIsInBhc3RlSUQiOjk0MDE3NDY3MCwiZGF0YVR5cGUiOiJzY2VuZSJ9Cg==(/figmeta)-->"></span><span data-buffer="<!--(figma)${nodeDataBase64}(/figma)-->"></span><span style="white-space:pre-wrap;"></span>`;
    return `<meta charset='utf-8'><meta charset="utf-8"><span data-metadata="<!--(figmeta)eyJmaWxlS2V5IjoiTkpUOTREZlhzMWJUZDB0V0c4OUc3NSIsInBhc3RlSUQiOjk0MDE3NDY3MCwiZGF0YVR5cGUiOiJzY2VuZSJ9Cg==(/figmeta)-->"></span><span data-buffer="<!--(figma)(/figma)-->"></span><span style="white-space:pre-wrap;"></span>`;
  }

  return (
    <div className="bg-white border border-gray-200 rounded-lg p-4 shadow-sm">
      <h3 className="font-medium text-lg mb-3">Figma Clipboard Tester</h3>
      
      <div className="mb-4">
        <label className="block text-sm font-medium text-gray-700 mb-1">Test Type:</label>
        <select 
          className="block w-full border border-gray-300 rounded-md px-3 py-2 text-sm"
          value={testType}
          onChange={(e) => setTestType(e.target.value)}
        >
          <option value="simpleBox">Simple Box</option>
        </select>
      </div>
      
      <div className="mb-4">
        <div 
          className="border border-gray-300 rounded-md w-full h-40 flex items-center justify-center"
          style={{ 
            backgroundColor: '#0070f3',
            borderRadius: '8px'
          }}
        >
          <span className="text-white font-medium">
            Simple Box (100×100px)
          </span>
        </div>
      </div>
      
      <button
        onClick={generateAndCopyClipboardData}
        className={`px-4 py-2 rounded text-white ${
          copied ? 'bg-green-500' : 'bg-blue-500 hover:bg-blue-600'
        }`}
      >
        {copied ? 'Copied!' : 'Generate & Copy Test Data'}
      </button>
      
      <p className="mt-3 text-sm text-gray-600">
        Click the button to generate test Figma clipboard data. After copying, paste directly into Figma to test.
      </p>
    </div>
  );
};

export default FigmaClipboardTester;